<?php

namespace Dizda\Bundle\AppBundle\Repository;

use Dizda\Bundle\AppBundle\Entity\AddressTransaction;
use Doctrine\Common\Collections\ArrayCollection;
use Doctrine\ORM\EntityRepository;

/**
 * DepositRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AddressTransactionRepository extends EntityRepository
{

    public function getSpendableTransactions()
    {
        $qb = $this->createQueryBuilder('at')
            ->andWhere('at.type = :type')
            ->andWhere('at.withdraw is NULL')
            ->andWhere('at.isSpent = false')
            ->setParameter('type', AddressTransaction::TYPE_IN)
        ;

        return $qb->getQuery()->execute();
    }

}
